import { useEffect, useState } from "react"
import { Carousel, Col, Container, Spinner, Row } from "react-bootstrap"
import { Navigate, useNavigate } from "react-router-dom"

const MoviesShows = ({ movie }) => {
  // state = {
  //   movies: [],
  //   isLoading: true
  // }

  const [movies, setMovies] = useState([])
  const [isloading, setIsloading] = useState(true)

  const navigate = useNavigate()

  const moviesFetch = async () => {
    try {
      let response = await fetch(
        "http://www.omdbapi.com/?i=tt3896198&apikey=712dba3e&s=" + movie
      )

      if (response.ok) {
        let data = await response.json()
        setMovies(data.Search)
      } else {
        alert("Something went wrong :(")
        setIsloading(false)
      }
    } catch (error) {
      alert(error)
      setIsloading(false)
    }
  }

  // componentDidMount = () => {
  //   this.moviesFetch()
  // }

  useEffect(() => {
    moviesFetch()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [])

  return (
    <Container fluid>
      {isLoading && <Spinner animation="border" variant="warning" />}
      <h4 className="d-flex my-3 movie-title">{movie}</h4>
      <Carousel indicators={false} className="my-2">
        <Carousel.Item>
          <Row className="d-flex justify-content-center ">
            {movies
              .filter((movie, index) => index < 5)
              .map((movie) => (
                <Col md={2} key={movie.imdbID}>
                  <img
                    src={movie.Poster}
                    alt={movie.Title}
                    className="carousel-img-style"
                    onClick={() => Navigate("/tv-shows/:" + movie.imdbID)}
                  />
                </Col>
              ))}
          </Row>
        </Carousel.Item>
        <Carousel.Item>
          <Row className="d-flex justify-content-center ">
            {movies
              .filter((movie, index) => index < 12 && index >= 5)
              .map((movie) => (
                <Col md={2} key={movie.imdbID}>
                  <img
                    src={movie.Poster}
                    alt={movie.Title}
                    className="carousel-img-style"
                    onClick={() => Navigate("/tv-shows/:" + movie.imdbID)}
                  />
                </Col>
              ))}
          </Row>
        </Carousel.Item>
      </Carousel>
    </Container>
  )
}
export default MoviesShows
