import { Component } from "react"
import { Carousel, Col, Container, Row } from "react-bootstrap"

class MoviesShows extends Component {
  state = {
    movies: []
  }

  moviesFetch = async () => {
    let response = await fetch(
      "http://www.omdbapi.com/?i=tt3896198&apikey=712dba3e&s=" +
        this.props.movie
    )

    let data = await response.json()
    this.setState({ movies: data.Search })
  }

  componentDidMount() {
    this.moviesFetch()
  }

  render() {
    return (
      <Container fluid>
        <h5>{this.props.movie}</h5>
        <Carousel indicators={false} className="my-2">
          <Carousel.Item>
            <Row>
              {this.state.movies
                .filter((movie, index) => index < 6)
                .map((movie) => (
                  <Col md={2} key={movie.imdbID}>
                    <img
                      src={movie.Poster}
                      alt={movie.Title}
                      className="carousel-img-style"
                    />
                  </Col>
                ))}
            </Row>
          </Carousel.Item>
          <Carousel.Item>
            <Row>
              {this.state.movies
                .filter((movie, index) => index < 12 && index >= 6)
                .map((movie) => (
                  <Col md={2} key={movie.imdbID}>
                    <img
                      src={movie.Poster}
                      alt={movie.Title}
                      className="carousel-img-style"
                    />
                  </Col>
                ))}
            </Row>
          </Carousel.Item>
        </Carousel>
      </Container>
    )
  }
}
export default MoviesShows
